import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import confetti from 'canvas-confetti';
import './DogSearch.css';

function DogSearch({ onLogout }) {
  const [dogs, setDogs] = useState([]);
  const [breeds, setBreeds] = useState([]);
  const [selectedBreed, setSelectedBreed] = useState('');
  const [zipFilter, setZipFilter] = useState('');
  const [ageMin, setAgeMin] = useState('');
  const [ageMax, setAgeMax] = useState('');
  const [favorites, setFavorites] = useState([]);
  const [matchedDog, setMatchedDog] = useState(null);
  const [recentlyViewed, setRecentlyViewed] = useState([]);
  const [locationMap, setLocationMap] = useState({});
  const [page, setPage] = useState(0);
  const [total, setTotal] = useState(0);
  const [sortOrder, setSortOrder] = useState('asc');
  const [showFavorites, setShowFavorites] = useState(false);
  const [zipError, setZipError] = useState('');
  const [toastName, setToastName] = useState('');
  const [flippedCards, setFlippedCards] = useState({});
  const size = 12;

  const matchRef = useRef(null);
  const favoritesRef = useRef(null);
  const location = useLocation();
  const navigate = useNavigate();

  const playBark = () => {
    const audio = new Audio('/bark.mp3');
    audio.play();
  };

  useEffect(() => {
    if (location.state?.userName) {
      setToastName(location.state.userName);
    }
    fetchBreeds();
    fetchDogs(true);
  }, []);

  useEffect(() => {
    fetchDogs();
  }, [sortOrder, page]);

  useEffect(() => {
    if (dogs.length) fetchLocations();
  }, [dogs]);

  useEffect(() => {
    const listener = (e) => {
      if (favoritesRef.current && !favoritesRef.current.contains(e.target)) {
        setShowFavorites(false);
      }
    };
    document.addEventListener('mousedown', listener);
    return () => document.removeEventListener('mousedown', listener);
  }, []);

  const fetchBreeds = async () => {
    try {
      const { data } = await axios.get('https://frontend-take-home-service.fetch.com/dogs/breeds', { withCredentials: true });
      setBreeds(data);
    } catch (err) {
      console.error("Breed fetch error:", err);
    }
  };

  const fetchLocations = async () => {
    try {
      const zips = [...new Set(dogs.map(d => d.zip_code))];
      const { data } = await axios.post('https://frontend-take-home-service.fetch.com/locations', zips, { withCredentials: true });
      const map = {};
      data.forEach(loc => map[loc.zip_code] = loc);
      setLocationMap(map);
    } catch (err) {
      console.error("Location fetch error:", err);
    }
  };

  const fetchDogs = async (ignoreEmpty = false) => {
    if (!ignoreEmpty && !zipFilter && !ageMin && !ageMax && !selectedBreed) return;

    if (zipFilter && !/^\d{5}$/.test(zipFilter)) {
      setZipError("Please enter a valid 5-digit ZIP code.");
      setDogs([]);
      setTotal(0);
      return;
    }

    let query = `?sort=breed:${sortOrder}&from=${page * size}&size=${size}`;
    if (selectedBreed) query += `&breeds=${selectedBreed}`;
    if (zipFilter) query += `&zipCodes=${zipFilter}`;
    if (ageMin) query += `&ageMin=${ageMin}`;
    if (ageMax) query += `&ageMax=${ageMax}`;

    try {
      const { data } = await axios.get(`https://frontend-take-home-service.fetch.com/dogs/search${query}`, { withCredentials: true });
      if (!data.resultIds.length) {
        setDogs([]);
        setTotal(0);
        return;
      }
      const res = await axios.post('https://frontend-take-home-service.fetch.com/dogs', data.resultIds, { withCredentials: true });
      setDogs(res.data);
      setTotal(data.total);
    } catch (err) {
      console.error("Dog fetch error:", err);
    }
  };

  const handleSearch = () => {
    setPage(0);
    fetchDogs();
  };

  const toggleFlip = (id) => {
    setFlippedCards(prev => ({ ...prev, [id]: !prev[id] }));
    const dog = dogs.find(d => d.id === id);
    if (dog) {
      setRecentlyViewed(prev => {
        const filtered = prev.filter(d => d.id !== dog.id);
        return [dog, ...filtered].slice(0, 10);
      });
    }
  };

  const handleMatch = async () => {
    try {
      const ids = favorites.map(f => f.id);
      const { data } = await axios.post('https://frontend-take-home-service.fetch.com/dogs/match', ids, { withCredentials: true });
      const match = await axios.post('https://frontend-take-home-service.fetch.com/dogs', [data.match], { withCredentials: true });
      setMatchedDog(match.data[0]);
      confetti();
      matchRef.current?.scrollIntoView({ behavior: 'smooth' });
    } catch (err) {
      console.error("Match error:", err);
    }
  };

  const renderPagination = () => {
    const totalPages = Math.ceil(total / size);
    if (totalPages <= 1) return null;

    const range = [...Array(totalPages)].map((_, i) => i);
    return (
      <div style={{ display: 'flex', justifyContent: 'center', gap: '10px', margin: '20px 0' }}>
        {page > 0 && <button onClick={() => setPage(page - 1)}>Prev</button>}
        {range.map(p => (
          <button key={p} onClick={() => setPage(p)} style={{ fontWeight: p === page ? 'bold' : 'normal' }}>{p + 1}</button>
        ))}
        {(page + 1) < totalPages && <button onClick={() => setPage(page + 1)}>Next</button>}
      </div>
    );
  };

  return (
    <div>
      <nav className="navbar">
        <div className="logo">🦴 FetchAFriend</div>
        {toastName && <div>👋 Welcome, {toastName}!</div>}
        <div>
          <button onClick={() => setShowFavorites(!showFavorites)}>❤️ Favorites ({favorites.length})</button>
          <button onClick={onLogout}>⏻ Logout</button>
        </div>
      </nav>

      <div className="filters">
        <select onChange={e => setSelectedBreed(e.target.value)} value={selectedBreed}>
          <option value="">All Breeds</option>
          {breeds.map(b => <option key={b} value={b}>{b}</option>)}
        </select>
        <input type="text" placeholder="ZIP" value={zipFilter} onChange={e => setZipFilter(e.target.value)} />
        <input type="number" placeholder="Min Age" value={ageMin} onChange={e => setAgeMin(e.target.value)} />
        <input type="number" placeholder="Max Age" value={ageMax} onChange={e => setAgeMax(e.target.value)} />
        <button onClick={handleSearch}>🔍 Search</button>
        <button onClick={() => setSortOrder(prev => prev === 'asc' ? 'desc' : 'asc')}>
          Sort: {sortOrder === 'asc' ? 'ASC' : 'DESC'}
        </button>
      </div>

      {zipError && <div style={{ color: 'red', textAlign: 'center' }}>{zipError}</div>}

      <div className="dog-list">
        {dogs.map(dog => (
          <div key={dog.id} className={`dog-card flip-card ${flippedCards[dog.id] ? 'flipped' : ''}`}>
            <div className="flip-card-inner">
              <div className="flip-card-front">
                <img src={dog.img} alt={dog.name} className="dog-image" />
                <p><strong>Breed:</strong> {dog.breed}</p>
                <button onClick={() => toggleFlip(dog.id)}>More Details</button>
              </div>
              <div className="flip-card-back">
                <h3>{dog.name}</h3>
                <p>Age: {dog.age}</p>
                <p>ZIP: {dog.zip_code}</p>
                <p>📍 <a href={`https://www.google.com/maps?q=${dog.zip_code}`} target="_blank" rel="noreferrer">View on Maps</a></p>
                <p><strong>Location:</strong> {locationMap[dog.zip_code]?.city}, {locationMap[dog.zip_code]?.state}</p>
                <button onClick={() => {
                  playBark();
                  setFavorites([...favorites, dog]);
                }}>❤️ Add to Favorites</button>
                <button onClick={() => toggleFlip(dog.id)}>Back</button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {renderPagination()}

      {recentlyViewed.length > 0 && (
        <div className="recently-viewed">
          <h4>📆 Recently Viewed</h4>
          <div style={{ display: 'flex', overflowX: 'auto', gap: '10px' }}>
            {recentlyViewed.map(dog => (
              <div key={dog.id} className="dog-card" style={{ minWidth: '180px' }}>
                <img src={dog.img} alt={dog.name} style={{ width: '100%' }} />
                <p>{dog.name}</p>
              </div>
            ))}
          </div>
        </div>
      )}

      {matchedDog && (
        <div ref={matchRef} className="dog-card match-card">
          <h3>🎯 Your Match</h3>
          <img src={matchedDog.img} alt={matchedDog.name} />
          <p>{matchedDog.name} - {matchedDog.breed}</p>
          <p>{matchedDog.age} years old</p>
          <p>{matchedDog.zip_code}</p>
        </div>
      )}
    </div>
  );
}

export default DogSearch;
