import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import './DogSearch.css';

function DogSearch({ onLogout }) {
  const [dogs, setDogs] = useState([]);
  const [breeds, setBreeds] = useState([]);
  const [locationMap, setLocationMap] = useState({});
  const [toastName, setToastName] = useState('');
  const [selectedBreed, setSelectedBreed] = useState('');
  const [zipFilter, setZipFilter] = useState('');
  const [ageMin, setAgeMin] = useState('');
  const [ageMax, setAgeMax] = useState('');
  const [favorites, setFavorites] = useState([]);
  const [matchedDog, setMatchedDog] = useState(null);
  const [sortOrder, setSortOrder] = useState('asc');
  const [page, setPage] = useState(0);
  const [total, setTotal] = useState(0);
  const [showFavorites, setShowFavorites] = useState(false);
  const [zipError, setZipError] = useState('');
  const [flippedCards, setFlippedCards] = useState({});
  const favoritesRef = useRef(null);
  const matchRef = useRef(null);
  const navigate = useNavigate();
  const location = useLocation();
  const size = 12;

  // Fetch breeds on mount
  useEffect(() => {
    fetchBreeds();
    if (location.state?.userName) setToastName(location.state.userName);
  }, []);

  // Fetch location info when dogs are updated
  useEffect(() => {
    if (dogs.length > 0) fetchLocationData();
  }, [dogs]);

  useEffect(() => {
    if (favorites.length === 0) setMatchedDog(null);
  }, [favorites]);

  // Hide favorites dropdown if clicked outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (favoritesRef.current && !favoritesRef.current.contains(event.target)) {
        setShowFavorites(false);
      }
    };
    if (showFavorites) {
      document.addEventListener('mousedown', handleClickOutside);
    }
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [showFavorites]);

  // Reset flipped cards on filter change
  useEffect(() => {
    setFlippedCards({});
  }, [page, selectedBreed, zipFilter, ageMin, ageMax]);

  const fetchBreeds = async () => {
    try {
      const { data } = await axios.get('https://frontend-take-home-service.fetch.com/dogs/breeds', { withCredentials: true });
      setBreeds(data);
    } catch (error) {
      console.error('❌ Error fetching breeds:', error);
      alert('Failed to fetch breed list.');
    }
  };

  const handleLogout = async () => {
    try {
      await axios.post('https://frontend-take-home-service.fetch.com/auth/logout', {}, { withCredentials: true });
      localStorage.clear();
      document.cookie = "";
      onLogout?.();
      navigate('/');
    } catch (error) {
      console.error('❌ Error during logout:', error);
      alert('Logout failed.');
    }
  };

  const handleSearchSubmit = () => {
    setPage(0);
    if (zipFilter && !/^\d{5}$/.test(zipFilter)) {
      setZipError('❌ Enter valid ZIP');
      setDogs([]);
      setTotal(0);
      return;
    }
    if (ageMin && ageMax && +ageMin > +ageMax) {
      setZipError('❌ Min age cannot exceed Max age');
      setDogs([]);
      setTotal(0);
      return;
    }
    setZipError('');
    fetchDogs();
  };

  const fetchDogs = async () => {
    try {
      let query = `?sort=breed:${sortOrder}&from=${page * size}&size=${size}`;
      if (selectedBreed) query += `&breeds=${selectedBreed}`;
      if (zipFilter) query += `&zipCodes=${zipFilter}`;
      if (ageMin) query += `&ageMin=${ageMin}`;
      if (ageMax) query += `&ageMax=${ageMax}`;

      const { data } = await axios.get(`https://frontend-take-home-service.fetch.com/dogs/search${query}`, { withCredentials: true });
      if (!Array.isArray(data.resultIds) || data.resultIds.length === 0) {
        setDogs([]);
        setTotal(0);
        return;
      }

      setTotal(data.total);

      const detailRes = await axios.post('https://frontend-take-home-service.fetch.com/dogs', data.resultIds, { withCredentials: true });
      if (Array.isArray(detailRes.data)) {
        setDogs(detailRes.data);
      } else {
        setDogs([]);
        console.warn("⚠️ Unexpected dogs detail response format:", detailRes);
      }
    } catch (error) {
      console.error('❌ Error fetching dogs:', error);
      setDogs([]);
      setTotal(0);
    }
  };

  const fetchLocationData = async () => {
    try {
      const zipCodes = [...new Set(dogs.map(dog => dog.zip_code))];
      if (!zipCodes.length) return;
      const { data } = await axios.post('https://frontend-take-home-service.fetch.com/locations', zipCodes, { withCredentials: true });
      const map = {};
      data.forEach(loc => (map[loc.zip_code] = loc));
      setLocationMap(map);
    } catch (error) {
      console.warn('⚠️ Error fetching location data:', error);
    }
  };

  const handleMatch = async () => {
    try {
      if (!favorites.length) return alert('Add at least 1 dog to match!');
      const ids = favorites.map(d => d.id);
      const matchRes = await axios.post('https://frontend-take-home-service.fetch.com/dogs/match', ids, { withCredentials: true });
      if (!matchRes.data.match) return alert('No match found.');
      const detail = await axios.post('https://frontend-take-home-service.fetch.com/dogs', [matchRes.data.match], { withCredentials: true });
      setMatchedDog(detail.data[0]);
      setTimeout(() => matchRef.current?.scrollIntoView({ behavior: 'smooth' }), 300);
    } catch (error) {
      console.error('❌ Error in match:', error);
      alert('Failed to generate match.');
    }
  };

  const toggleFlip = id => {
    setFlippedCards(prev => ({ ...prev, [id]: !prev[id] }));
  };

  const renderPagination = () => {
    if (total === 0) return null;
    const totalPages = Math.ceil(total / size);
    const visiblePages = [];
    const start = Math.max(1, page + 1 - 2);
    const end = Math.min(start + 4, totalPages);

    for (let i = start; i <= end; i++) {
      visiblePages.push(i);
    }

    return (
      <div style={{ display: 'flex', justifyContent: 'center', flexWrap: 'wrap', gap: '8px', marginTop: '25px' }}>
        <button onClick={() => setPage(p => Math.max(p - 1, 0))} disabled={page === 0}>◀</button>
        {start > 1 && <span>...</span>}
        {visiblePages.map(p => (
          <button
            key={p}
            onClick={() => setPage(p - 1)}
            style={{ fontWeight: p - 1 === page ? 'bold' : 'normal' }}
          >
            {p}
          </button>
        ))}
        {end < totalPages && <span>...</span>}
        <button onClick={() => setPage(p => p + 1)} disabled={(page + 1) * size >= total}>▶</button>
      </div>
    );
  };

  return (
    <div style={{ padding: '10px' }}>
      <nav className="navbar">
        <div style={{ fontWeight: 'bold' }}>🦴 FetchAFriend</div>
        {toastName && <div style={{ fontWeight: 'bold' }}>Welcome, {toastName}</div>}
        <div>
          <button onClick={() => setShowFavorites(!showFavorites)}>❤️ Favorites ({favorites.length})</button>
          <button onClick={handleLogout}>⏻ Logout</button>
        </div>
      </nav>

      <div style={{ textAlign: 'center', marginTop: '15px' }}>
        <select onChange={(e) => setSelectedBreed(e.target.value)} defaultValue="">
          <option value="">All Breeds</option>
          {breeds.map(b => <option key={b} value={b}>{b}</option>)}
        </select>
        <input placeholder="ZIP" value={zipFilter} onChange={(e) => setZipFilter(e.target.value)} />
        <input placeholder="Min Age" type="number" value={ageMin} onChange={(e) => setAgeMin(e.target.value)} />
        <input placeholder="Max Age" type="number" value={ageMax} onChange={(e) => setAgeMax(e.target.value)} />
        <button onClick={() => setSortOrder(prev => (prev === 'asc' ? 'desc' : 'asc'))}>
          Sort: {sortOrder === 'asc' ? 'ASC' : 'DESC'}
        </button>
        <button onClick={handleSearchSubmit} style={{ backgroundColor: '#70542f', color: 'white', marginLeft: '10px' }}>
          🔍 Search
        </button>
        {zipError && <div style={{ color: 'red' }}>{zipError}</div>}
      </div>

      <div className="dog-list">
        {dogs.map((dog) => (
          <div key={dog.id} className="dog-card">
            <img src={dog.img} alt={dog.name} />
            <h4>{dog.name}</h4>
            <p>{dog.breed}</p>
            <p>{locationMap[dog.zip_code]?.city || ''}, {locationMap[dog.zip_code]?.state || ''}</p>
            <button onClick={() => toggleFlip(dog.id)}>Flip</button>
            <button onClick={() => setFavorites([...favorites, dog])}>❤️ Favorite</button>
          </div>
        ))}
      </div>

      {renderPagination()}

      {matchedDog && (
        <div ref={matchRef} style={{ marginTop: '30px' }}>
          <h3>🎯 Matched Dog</h3>
          <p>{matchedDog.name}</p>
          <p>{matchedDog.breed}</p>
          <img src={matchedDog.img} alt={matchedDog.name} width="200" />
        </div>
      )}
    </div>
  );
}

export default DogSearch;
