import confetti from 'canvas-confetti';
import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import './DogSearch.css';

function DogSearch({ onLogout }) {
  const [dogs, setDogs] = useState([]);
  const [breeds, setBreeds] = useState([]);
  const [locationMap, setLocationMap] = useState({});
  const [toastName, setToastName] = useState('');
  const [selectedBreed, setSelectedBreed] = useState('');
  const [zipFilter, setZipFilter] = useState('');
  const [ageMin, setAgeMin] = useState('');
  const [ageMax, setAgeMax] = useState('');
  const [favorites, setFavorites] = useState([]);
  const [matchedDog, setMatchedDog] = useState(null);
  const [recentlyViewed, setRecentlyViewed] = useState([]);
  const [sortOrder, setSortOrder] = useState('asc');
  const [page, setPage] = useState(0);
  const [total, setTotal] = useState(0);
  const [showFavorites, setShowFavorites] = useState(false);
  const [zipError, setZipError] = useState('');
  const [flippedCards, setFlippedCards] = useState({});

  const favoritesRef = useRef(null);
  const matchRef = useRef(null);

  const navigate = useNavigate();
  const location = useLocation();
  const size = 12;

  const playBark = () => {
    const audio = new Audio('/bark.mp3');
    audio.play();
  };  

  useEffect(() => {
    fetchBreeds();
    if (location.state?.userName) setToastName(location.state.userName);
  }, []);

  useEffect(() => {
    const timer = setTimeout(fetchDogs, 300);
    return () => clearTimeout(timer);
  }, [selectedBreed, sortOrder, page, zipFilter, ageMin, ageMax]);

  useEffect(() => {
    if (dogs.length > 0) fetchLocationData();
  }, [dogs]);

  useEffect(() => {
    if (favorites.length === 0) setMatchedDog(null);
  }, [favorites]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (favoritesRef.current && !favoritesRef.current.contains(event.target)) {
        setShowFavorites(false);
      }
    };
    if (showFavorites) document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [showFavorites]);

  useEffect(() => {
    setFlippedCards({});
  }, [page, selectedBreed, zipFilter, ageMin, ageMax]);

  const handleLogout = async () => {
    try {
      await axios.post('https://frontend-take-home-service.fetch.com/auth/logout', {}, { withCredentials: true });
      onLogout?.();
      navigate('/');
    } catch {
      alert('Logout failed.');
    }
  };

  const fetchBreeds = async () => {
    try {
      const { data } = await axios.get('https://frontend-take-home-service.fetch.com/dogs/breeds', { withCredentials: true });
      setBreeds(data);
    } catch {
      alert('Failed to fetch breeds.');
    }
  };

  const fetchDogs = async () => {
    if (zipFilter && !/^\d{5}$/.test(zipFilter)) {
      setZipError('Please enter a valid 5-digit ZIP code.');
      setDogs([]);
      return;
    }
    if (ageMin && ageMax && +ageMin > +ageMax) {
      setZipError('Min age cannot be greater than max age.');
      setDogs([]);
      return;
    }
    setZipError('');

    try {
      let query = `?sort=breed:${sortOrder}&from=${page * size}&size=${size}`;
      if (selectedBreed) query += `&breeds=${selectedBreed}`;
      if (zipFilter) query += `&zipCodes=${zipFilter.trim()}`;
      if (ageMin) query += `&ageMin=${ageMin}`;
      if (ageMax) query += `&ageMax=${ageMax}`;

      const { data } = await axios.get(`https://frontend-take-home-service.fetch.com/dogs/search${query}`, { withCredentials: true });
      setTotal(data.total);
      const dogDetails = await axios.post('https://frontend-take-home-service.fetch.com/dogs', data.resultIds, { withCredentials: true });
      setDogs(dogDetails.data);

      if (dogDetails.data.length === 0) {
        setZipError('No dogs found for the given filters.');
      }
    } catch {
      alert('Failed to fetch dogs.');
    }
  };

  const fetchLocationData = async () => {
    const zipCodes = [...new Set(dogs.map(dog => dog.zip_code))];
    if (!zipCodes.length) return;
    try {
      const { data } = await axios.post('https://frontend-take-home-service.fetch.com/locations', zipCodes, { withCredentials: true });
      const map = {};
      data.forEach(loc => map[loc.zip_code] = loc);
      setLocationMap(map);
    } catch (err) {
      console.warn('Failed to fetch location data:', err);
    }
  };

  const handleMatch = async () => {
    if (favorites.length === 0) {
      alert('Please add at least one dog to favorites!');
      return;
    }

    try {
      const ids = favorites.map(d => d.id);
      const { data } = await axios.post('https://frontend-take-home-service.fetch.com/dogs/match', ids, {
        withCredentials: true,
      });

      const matchRes = await axios.post('https://frontend-take-home-service.fetch.com/dogs', [data.match], {
        withCredentials: true,
      });

      const matchedDogData = matchRes.data[0];
      setMatchedDog(matchedDogData);

      // ‚úÖ Fetch location for matched dog ZIP
      const locationRes = await axios.post(
        'https://frontend-take-home-service.fetch.com/locations',
        [matchedDogData.zip_code],
        { withCredentials: true }
      );

      setLocationMap(prev => ({
        ...prev,
        [matchedDogData.zip_code]: locationRes.data[0]
      }));

      // Scroll smoothly to match card
      setTimeout(() => matchRef.current?.scrollIntoView({ behavior: 'smooth' }), 300);
    } catch (error) {
      alert('Failed to generate match.');
    }
  };



  const handleFeelingLucky = () => {
    if (dogs.length === 0) return;
    const randomDog = dogs[Math.floor(Math.random() * dogs.length)];
    setFlippedCards(prev => ({ ...prev, [randomDog.id]: true }));
    setTimeout(() => {
      const element = document.getElementById(`dog-${randomDog.id}`);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth', block: 'center' });
      }
    }, 100);
  };


  const toggleFlip = id => {
    setFlippedCards(prev => ({ ...prev, [id]: !prev[id] }));
    setRecentlyViewed(prev => {
      const dog = dogs.find(d => d.id === id);
      if (!dog) return prev;
      const filtered = prev.filter(d => d.id !== dog.id);
      return [dog, ...filtered].slice(0, 10); // add on top, remove duplicates, keep max 10
    });

  };  

  const renderPagination = () => {
    const totalPages = Math.ceil(total / size);
    const maxVisible = 5;
    const pageNumbers = [];

    let startPage = Math.max(1, page + 1 - Math.floor(maxVisible / 2));
    let endPage = startPage + maxVisible - 1;

    if (endPage > totalPages) {
      endPage = totalPages;
      startPage = Math.max(1, endPage - maxVisible + 1);
    }

    for (let i = startPage; i <= endPage; i++) {
      pageNumbers.push(i);
    }

    return (
      <div style={{ display: 'flex', justifyContent: 'center', gap: '10px', marginTop: '20px', flexWrap: 'wrap' }}>
        <button
          onClick={() => setPage(prev => Math.max(0, prev - 1))}
          disabled={page === 0}
          style={navBtnStyle('#80A8E4', page === 0)}
        >
          ‚óÄ Prev
        </button>

        {startPage > 1 && (
          <>
            <button onClick={() => setPage(0)} style={pageButtonStyle(0)}>1</button>
            <span style={{ alignSelf: 'center' }}>...</span>
          </>
        )}

        {pageNumbers.map(p => (
          <button
            key={p}
            onClick={() => setPage(p - 1)}
            style={pageButtonStyle(p - 1, page)}
          >
            {p}
          </button>
        ))}

        {endPage < totalPages && (
          <>
            <span style={{ alignSelf: 'center' }}>...</span>
            <button onClick={() => setPage(totalPages - 1)} style={pageButtonStyle(totalPages - 1)}>{totalPages}</button>
          </>
        )}

        <button
          onClick={() => setPage(prev => prev + 1)}
          disabled={(page + 1) * size >= total}
          style={navBtnStyle('#001F54', (page + 1) * size >= total)}
        >
          Next ‚ñ∂
        </button>
      </div>
    );
  };

  const pageButtonStyle = (btnPage, currentPage = page) => ({
    backgroundColor: btnPage === currentPage ? '#8E4585' : '#f0f0f0',
    color: btnPage === currentPage ? '#fff' : '#000',
    border: 'none',
    borderRadius: '6px',
    padding: '6px 12px',
    cursor: 'pointer',
    fontWeight: btnPage === currentPage ? 'bold' : 'normal'
  });

  const navBtnStyle = (color, disabled) => ({
    backgroundColor: color,
    color: 'white',
    border: 'none',
    borderRadius: '6px',
    padding: '6px 12px',
    cursor: disabled ? 'not-allowed' : 'pointer'
  });

  const fullWidthButtonStyle = {
    width: 'calc(100% - 40px)',
    marginLeft: '10px',
    marginTop: '10px',
    padding: '8px',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  };

  return (
    <>

    <nav
      className="navbar"
      style={{
        backgroundColor: '#FFB400', 
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '10px 20px',
        color: '#fff'
      }}
    >
      <div style={{ fontWeight: 'bold', fontSize: '24px' }}>ü¶¥ FetchAFriend</div>

      {toastName && (
        <div style={{ position: 'absolute', left: '50%', transform: 'translateX(-50%)', color: '#70542f', fontWeight: 'bold', fontSize: '24px' }}>
          üëã Welcome, {toastName}!
        </div>
      )}

      <div className="nav-buttons" style={{ display: 'flex', gap: '10px' }}>
        <button onClick={() => setShowFavorites(!showFavorites)}>‚ù§Ô∏è Favorites ({favorites.length})</button>
        <button onClick={handleLogout}>‚èª Logout</button>
      </div>
    </nav>
      <div style={{ textAlign: 'center', marginTop: '10px' }}>
        <button
          onClick={handleFeelingLucky}
          style={{
            backgroundColor: '#FFB400',
            color: '#fff',
            padding: '10px 20px',
            border: 'none',
            borderRadius: '8px',
            fontWeight: 'bold',
            cursor: 'pointer',
            boxShadow: '0 4px 8px rgba(0,0,0,0.1)'
          }}
        >
          üé≤ Feeling Lucky?
        </button>
      </div>



      {showFavorites && (
  <div
    ref={favoritesRef}
    className="favorites-dropdown"
    style={{
      transition: 'all 0.3s ease',
      position: 'absolute',
      top: '70px',
      right: '110px',
      width: '250px',
      maxHeight: '300px',
      overflowY: 'auto',
      background: '#fff',
      border: '1px solid #ccc',
      borderRadius: '10px',
      boxShadow: '0 0 10px rgba(0,0,0,0.2)',
      zIndex: 1000,
      padding: '10px'
    }}
  >
    <h4 style={{ margin: '0 0 10px 0', textAlign: 'center' }}>‚ù§Ô∏è Favorites</h4>
    {favorites.length === 0 ? (
      <p style={{ textAlign: 'center', fontStyle: 'italic' }}>No favorites yet</p>
    ) : (
      <>
        {favorites.map(dog => (
          <div
            key={dog.id}
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '10px',
              marginBottom: '10px',
              borderBottom: '1px solid #eee',
              paddingBottom: '5px',
              position: 'relative'
            }}
          >
            <img
              src={dog.img}
              alt={dog.name}
              width="40"
              height="40"
              style={{ borderRadius: '5px', objectFit: 'cover' }}
            />
            <div style={{ flexGrow: 1 }}>
              <strong>{dog.name}</strong>
              <p style={{ fontSize: '12px', margin: 0 }}>{dog.breed}</p>
            </div>
            <button
              onClick={() => {
                const confirmRemove = window.confirm(`Are you sure you want to remove ${dog.name} from favorites?`);
                if (confirmRemove) {
                  setFavorites(favorites.filter(fav => fav.id !== dog.id));
                }
              }}
              style={{
                position: 'absolute',
                top: '0px',
                right: '0px',
                background: 'transparent',
                border: 'none',
                fontSize: '14px',
                color: '#cc0000',
                cursor: 'pointer'
              }}
            >
              ‚ùå
            </button>
          </div>
        ))}

        <button
          style={{ ...fullWidthButtonStyle, backgroundColor: '#cc0000' }}
          onClick={() => {
            const confirmClear = window.confirm('Are you sure you want to remove all favorites?');
            if (confirmClear) setFavorites([]);
          }}
        >
          üóëÔ∏è Clear All
        </button>


        <button
          style={{ ...fullWidthButtonStyle, backgroundColor: '#8E4585' }}
          onClick={handleMatch}
        >
          üéØ Find Match
        </button>

      </>
      )}
    </div>
  )}

      <div className="filters" style={{ textAlign: 'center', marginBottom: '20px' }}>
        <select onChange={(e) => setSelectedBreed(e.target.value)} defaultValue="">
          <option value="">All Breeds</option>
          {breeds.map((breed) => (
            <option key={breed} value={breed}>{breed}</option>
          ))}
        </select>
        <input
          type="text"
          placeholder="Filter by ZIP code"
          value={zipFilter}
          onChange={(e) => setZipFilter(e.target.value)}
          style={{ marginLeft: '10px', padding: '5px', borderRadius: '6px', border: '1px solid #ccc', width: '130px' }}
        />
        <input
          type="number"
          placeholder="Min Age"
          value={ageMin}
          onChange={(e) => setAgeMin(e.target.value)}
          style={{ marginLeft: '10px', padding: '5px', borderRadius: '6px', border: '1px solid #ccc', width: '80px' }}
        />
        <input
          type="number"
          placeholder="Max Age"
          value={ageMax}
          onChange={(e) => setAgeMax(e.target.value)}
          style={{ marginLeft: '10px', padding: '5px', borderRadius: '6px', border: '1px solid #ccc', width: '80px' }}
        />
        <button onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')} style={{ marginLeft: '10px', backgroundColor: '#8E4585', color: 'white', border: 'none', padding: '6px 12px', borderRadius: '6px', cursor: 'pointer' }}>
          Sort: {sortOrder === 'asc' ? 'DESC' : 'ASC'}
        </button>
        {zipError && (
          <div style={{ color: 'red', marginTop: '8px', fontSize: '14px' }}>{zipError}</div>
        )}
      </div>

      <div className="dog-list" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '30px', justifyContent: 'center', padding: '0 20px' }}>
        {dogs.map(dog => (
          <div key={dog.id} id={`dog-${dog.id}`} className={`dog-card flip-card ${flippedCards[dog.id] ? 'flipped' : ''}`}>
            <div className="flip-card-inner">
              <div className="flip-card-front">
                <img src={dog.img} alt={dog.name} className="dog-image" style={{ width: '100%', height: '180px', objectFit: 'cover', margin: '0 auto', display: 'block', borderRadius: '8px' }} />
                <p><strong>Breed:</strong> {dog.breed}</p>
                <button onClick={() => toggleFlip(dog.id)}style={{ backgroundColor: '#8E4585', color: 'white', border: 'none', padding: '6px 12px', borderRadius: '6px', cursor: 'pointer' }}
                >More Details</button>
              </div>
              <div
                className="flip-card-back"
                style={{
                  border: '2px solid #ccc',
                  borderRadius: '10px',
                  padding: '10px',
                  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                  backgroundColor: '#fff'
                }}
              >

                <h3>{dog.name}</h3>
                <p><strong>Age:</strong> {dog.age}</p>
                <p><strong>ZIP:</strong> {dog.zip_code}</p>
                <p>üìç <a href={`https://www.google.com/maps?q=${dog.zip_code}`} target="_blank" rel="noreferrer">View on Maps</a></p>
                <p><strong>Location:</strong> {locationMap[dog.zip_code] ? `${locationMap[dog.zip_code].city}, ${locationMap[dog.zip_code].state}` : 'Loading...'}</p>
                {favorites.some(fav => fav.id === dog.id) ? (
                  <button disabled>‚úÖ Favorited</button>
                ) : (
                  <button
                    onClick={() => {
                      playBark();
                      setFavorites([...favorites, dog]);
                    }}
                    style={{
                      backgroundColor: '#001F54',
                      color: 'white',
                      border: 'none',
                      padding: '6px 12px',
                      borderRadius: '6px',
                      cursor: 'pointer'
                    }}
                  >
                    ‚ù§Ô∏è Add to Favorites
                  </button>


                )}
                <button
                  onClick={() => toggleFlip(dog.id)}
                  style={{
                    marginTop: '5px',
                    backgroundColor: '#001F54',
                    color: 'white',
                    border: 'none',
                    padding: '6px 12px',
                    borderRadius: '6px',
                    cursor: 'pointer'
                  }}
                >
                  Back
                </button>

              </div>
            </div>
          </div>
        ))}
      </div>

      {renderPagination()}

      {recentlyViewed.length > 0 && (
      <div className="recently-viewed" style={{ marginTop: '40px', padding: '0 20px' }}>
        <h4 style={{ textAlign: 'center', fontSize: '24px', marginBottom: '10px' }}>üìÜ Recently Viewed</h4>
        <div style={{
          display: 'flex',
          gap: '15px',
          overflowX: 'auto',
          paddingBottom: '10px'
        }}>
          {recentlyViewed.map(dog => (
            <div
              key={dog.id}
              className="dog-card"
              style={{
                minWidth: '200px',
                flex: '0 0 auto'
              }}
            >
              <img src={dog.img} alt={dog.name} className="dog-image" style={{ width: '100%', height: '140px', objectFit: 'cover', borderRadius: '8px' }} />
              <p>{dog.name}</p>
            </div>
          ))}
        </div>
      </div>
    )}


      {matchedDog && (
      <div ref={matchRef} style={{ textAlign: 'center', marginTop: '50px' }}>
        <h3 style={{ fontSize: '22px', marginBottom: '15px', color: '#e63946' }}>üéØ Your Perfect Match</h3>
        <div style={{ display: 'flex', justifyContent: 'center' }}>
          <div className="dog-card match-card">
            <img
              src={matchedDog.img}
              alt={matchedDog.name}
              className="dog-image"
              style={{
                width: '100%',
                height: '180px',
                objectFit: 'cover',
                borderRadius: '8px',
                marginBottom: '10px'
              }}
            />
            <h3>{matchedDog.name}</h3>
            <p><strong>Breed:</strong> {matchedDog.breed}</p>
            <p><strong>Age:</strong> {matchedDog.age}</p>
            <p><strong>ZIP:</strong> {matchedDog.zip_code}</p>
            <p>
              <strong>Location:</strong>{' '}
              {locationMap[matchedDog.zip_code]
                ? `${locationMap[matchedDog.zip_code].city}, ${locationMap[matchedDog.zip_code].state}`
                : 'Loading...'}
            </p>
          </div>
        </div>
      </div>
    )}


    </>
  );
}

export default DogSearch;
