import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login({ onLogin }) {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const navigate = useNavigate();


  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('https://frontend-take-home-service.fetch.com/auth/login', {
        name,
        email
      }, { withCredentials: true });

      onLogin();
      navigate('/search', { state: { userName: name } });
    } catch (error) {
      alert('Login failed. Please check your credentials.');
    }
  };

  return (
    <div style={{ height: '100vh', overflow: 'hidden' }}>
      <div style={{
        width: '100vw',
        height: '100vh',
        display: 'flex',
        overflow: 'hidden'
      }}>
        {/* Marquee */}
        <div style={{
          width: '100%',
          background: '#FFB400',
          color: 'white',
          padding: '10px 0',
          fontWeight: 'bold',
          fontSize: '16px',
          textAlign: 'center',
          whiteSpace: 'nowrap',
          overflow: 'hidden',
          position: 'absolute',
          top: 0,
          left: 0,
          zIndex: 10
        }}>
          <div style={{
            display: 'inline-block',
            paddingLeft: '100%',
            animation: 'scroll-left 30s linear infinite'
          }}>
            üêæ Adopt. Match. Love. | Built by Sravya Koyi | Powered by Fetch | Find your furry soulmate today! üêæ
          </div>
        </div>

        {/* Left - Dog image */}
        <div style={{
          width: '60%',
          height: '100%',
          backgroundImage: 'url("https://images.unsplash.com/photo-1583337130417-3346a1be7dee?auto=format&fit=crop&w=1350&q=80")',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat'
        }} />

        {/* Right - Login form */}
        <div style={{
          width: '40%',
          height: '100%',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          background: '#f9f9f9'
        }}>
          <form
            onSubmit={handleSubmit}
            style={{
              background: '#fff',
              padding: '40px',
              borderRadius: '12px',
              boxShadow: '0 0 20px rgba(0, 0, 0, 0.1)',
              display: 'flex',
              flexDirection: 'column',
              gap: '15px',
              width: '100%',
              maxWidth: '400px',
              zIndex: 5
            }}
          >
            <h2 style={{ textAlign: 'center', marginBottom: '10px' }}>ü¶¥ FetchAFriend</h2>
            <input
              type="text"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              style={{
                padding: '10px',
                borderRadius: '8px',
                border: '1px solid #ccc',
                fontSize: '16px'
              }}
            />
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              style={{
                padding: '10px',
                borderRadius: '8px',
                border: '1px solid #ccc',
                fontSize: '16px'
              }}
            />
            <button
              type="submit"
              style={{
                padding: '12px',
                backgroundColor: '#70542f', //#5f4214
                color: '#fff',
                fontWeight: 'bold',
                fontSize: '16px',
                border: 'none',
                borderRadius: '8px',
                cursor: 'pointer',
                transition: 'background 0.3s ease'
              }}
            >
              üîê Login
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
