import confetti from 'canvas-confetti';
import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import './DogSearch.css';

function DogSearch({ onLogout }) {
  const [dogs, setDogs] = useState([]);
  const [breeds, setBreeds] = useState([]);
  const [locationMap, setLocationMap] = useState({});
  const [toastName, setToastName] = useState('');
  const [selectedBreed, setSelectedBreed] = useState('');
  const [zipFilter, setZipFilter] = useState('');
  const [ageMin, setAgeMin] = useState('');
  const [ageMax, setAgeMax] = useState('');
  const [favorites, setFavorites] = useState([]);
  const [matchedDog, setMatchedDog] = useState(null);
  const [recentlyViewed, setRecentlyViewed] = useState([]);
  const [sortOrder, setSortOrder] = useState('asc');
  const [page, setPage] = useState(0);
  const [total, setTotal] = useState(0);
  const [showFavorites, setShowFavorites] = useState(false);
  const [zipError, setZipError] = useState('');
  const [flippedCards, setFlippedCards] = useState({});

  const favoritesRef = useRef(null);
  const matchRef = useRef(null);

  const navigate = useNavigate();
  const location = useLocation();
  const size = 12;

  const playBark = () => {
    const audio = new Audio('/bark.mp3');
    audio.play();
  };  

  useEffect(() => {
    fetchBreeds();
    if (location.state?.userName) setToastName(location.state.userName);
  }, []);

  useEffect(() => {
    if (selectedBreed || sortOrder) fetchDogs(); // for breed/sort
  }, [selectedBreed, sortOrder, page]);


  useEffect(() => {
    if (dogs.length > 0) fetchLocationData();
  }, [dogs]);

  useEffect(() => {
    if (favorites.length === 0) setMatchedDog(null);
  }, [favorites]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (favoritesRef.current && !favoritesRef.current.contains(event.target)) {
        setShowFavorites(false);
      }
    };
    if (showFavorites) document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [showFavorites]);

  useEffect(() => {
    setFlippedCards({});
  }, [page, selectedBreed, zipFilter, ageMin, ageMax]);

  const handleLogout = async () => {
    try {
      await axios.post('https://frontend-take-home-service.fetch.com/auth/logout', {}, { withCredentials: true });

      // Clear localStorage/cookies/etc.
      localStorage.clear();
      document.cookie = ""; // Optional manual cookie clear

      if (onLogout) onLogout();
      navigate('/');
    } catch (err) {
      console.error("‚èª Logout failed:", err);
      alert('Logout failed. Please try again.');
    }
  };


  const fetchBreeds = async () => {
    try {
      const res = await axios.get('https://frontend-take-home-service.fetch.com/dogs/breeds', { withCredentials: true });
      setBreeds(res.data);
    } catch (err) {
      console.error("üêæ Failed to fetch breeds:", err);
      alert("Could not load breed list. Try again later.");
    }
  };

  const handleSearchSubmit = () => {
    if (!zipFilter && !ageMin && !ageMax) {
      alert("Please enter ZIP code or Age range to search.");
      return;
    }

    // validate ZIP if entered
    if (zipFilter && !/^\d{5}$/.test(zipFilter)) {
      setZipError("Please enter a valid 5-digit ZIP code.");
      setDogs([]);
      setTotal(0);
      setPage(0);
      return;
    }

    if (ageMin && ageMax && parseInt(ageMin) > parseInt(ageMax)) {
      setZipError("Min age cannot be greater than Max age.");
      setDogs([]);
      setTotal(0);
      setPage(0);
      return;
    }

    setPage(0);  // reset page before new search
    fetchDogs(); // finally fetch
  };

  const handleSearchFilters = () => {
    setPage(0); // reset pagination to page 0 on new filter search

    // ZIP validation
    if (zipFilter && !/^\d{5}$/.test(zipFilter)) {
      setZipError("Please enter a valid 5-digit ZIP code.");
      setDogs([]);
      setTotal(0);
      return;
    }

    // Age validation
    if (ageMin && ageMax && parseInt(ageMin) > parseInt(ageMax)) {
      setZipError("Min age cannot be greater than max age.");
      setDogs([]);
      setTotal(0);
      return;
    }

    setZipError('');
    fetchDogs();
  };



  const fetchDogs = async () => {
    try {
      let query = `?sort=breed:${sortOrder}&from=${page * size}&size=${size}`;
      if (selectedBreed) query += `&breeds=${selectedBreed}`;
      if (zipFilter) query += `&zipCodes=${zipFilter}`;
      if (ageMin) query += `&ageMin=${ageMin}`;
      if (ageMax) query += `&ageMax=${ageMax}`;

      const res = await axios.get(`https://frontend-take-home-service.fetch.com/dogs/search${query}`, { withCredentials: true });

      if (!res.data || !res.data.resultIds || res.data.resultIds.length === 0) {
        setDogs([]);
        setTotal(0);
        console.warn("No dogs found for this filter.");
        return;
      }

      setTotal(res.data.total);
      const dogRes = await axios.post('https://frontend-take-home-service.fetch.com/dogs', res.data.resultIds, { withCredentials: true });
      setDogs(dogRes.data);
      setZipError('');
    } catch (error) {
      console.error("Failed to fetch dogs:", error);
      setZipError("Failed to fetch dogs. Please try again later.");
      setDogs([]);
      setTotal(0);
    }
  };  

  const fetchLocationData = async () => {
    try {
      const zipCodes = [...new Set(dogs.map(dog => dog.zip_code))];
      if (zipCodes.length === 0) return;

      const res = await axios.post('https://frontend-take-home-service.fetch.com/locations', zipCodes, { withCredentials: true });

      const map = {};
      res.data.forEach(loc => {
        map[loc.zip_code] = loc;
      });

      setLocationMap(map);
    } catch (err) {
      console.error("üìç Failed to fetch location data:", err);
    }
  };


  const handleMatch = async () => {
    if (favorites.length === 0) {
      alert('Please add at least one dog to favorites!');
      return;
    }

    try {
      const dogIds = favorites.map(dog => dog.id);
      const { data } = await axios.post('https://frontend-take-home-service.fetch.com/dogs/match', dogIds, { withCredentials: true });

      if (!data.match) {
        console.warn("No match found from API response");
        return;
      }

      const dogRes = await axios.post('https://frontend-take-home-service.fetch.com/dogs', [data.match], { withCredentials: true });
      setMatchedDog(dogRes.data[0]);

      setTimeout(() => {
        matchRef.current?.scrollIntoView({ behavior: 'smooth' });
      }, 300);
    } catch (err) {
      console.error("üéØ Match error:", err);
      alert('Failed to generate match. Try again later.');
    }
  };  


  const handleFeelingLucky = () => {
    if (dogs.length === 0) return;
    const randomDog = dogs[Math.floor(Math.random() * dogs.length)];
    setFlippedCards(prev => ({ ...prev, [randomDog.id]: true }));
    setTimeout(() => {
      const element = document.getElementById(`dog-${randomDog.id}`);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth', block: 'center' });
      }
    }, 100);
  };


  const toggleFlip = id => {
    setFlippedCards(prev => ({ ...prev, [id]: !prev[id] }));
    setRecentlyViewed(prev => {
      const dog = dogs.find(d => d.id === id);
      if (!dog) return prev;
      const filtered = prev.filter(d => d.id !== dog.id);
      return [dog, ...filtered].slice(0, 10); // add on top, remove duplicates, keep max 10
    });

  };  

  const renderPagination = () => {
    const totalPages = Math.ceil(total / size);
    const maxVisible = 5;
    const pageNumbers = [];

    let startPage = Math.max(1, page + 1 - Math.floor(maxVisible / 2));
    let endPage = startPage + maxVisible - 1;

    if (endPage > totalPages) {
      endPage = totalPages;
      startPage = Math.max(1, endPage - maxVisible + 1);
    }

    for (let i = startPage; i <= endPage; i++) {
      pageNumbers.push(i);
    }

    return total > 0 ? (
      <div style={{ display: 'flex', justifyContent: 'center', gap: '10px', marginTop: '20px', flexWrap: 'wrap' }}>
        <button
          onClick={() => setPage(prev => Math.max(0, prev - 1))}
          disabled={page === 0}
          style={navBtnStyle('#80A8E4', page === 0)}
        >
          ‚óÄ Prev
        </button>

        {startPage > 1 && (
          <>
            <button onClick={() => setPage(0)} style={pageButtonStyle(0)}>1</button>
            <span style={{ alignSelf: 'center' }}>...</span>
          </>
        )}

        {pageNumbers.map(p => (
          <button
            key={p}
            onClick={() => setPage(p - 1)}
            style={pageButtonStyle(p - 1, page)}
          >
            {p}
          </button>
        ))}

        {endPage < totalPages && (
          <>
            <span style={{ alignSelf: 'center' }}>...</span>
            <button onClick={() => setPage(totalPages - 1)} style={pageButtonStyle(totalPages - 1)}>{totalPages}</button>
          </>
        )}

        <button
          onClick={() => setPage(prev => prev + 1)}
          disabled={(page + 1) * size >= total}
          style={navBtnStyle('#001F54', (page + 1) * size >= total)}
        >
          Next ‚ñ∂
        </button>
      </div>
    ) : null;    
  };

  const pageButtonStyle = (btnPage, currentPage = page) => ({
    backgroundColor: btnPage === currentPage ? '#8E4585' : '#f0f0f0',
    color: btnPage === currentPage ? '#fff' : '#000',
    border: 'none',
    borderRadius: '6px',
    padding: '6px 12px',
    cursor: 'pointer',
    fontWeight: btnPage === currentPage ? 'bold' : 'normal'
  });

  const navBtnStyle = (color, disabled) => ({
    backgroundColor: color,
    color: 'white',
    border: 'none',
    borderRadius: '6px',
    padding: '6px 12px',
    cursor: disabled ? 'not-allowed' : 'pointer'
  });

  const fullWidthButtonStyle = {
    width: 'calc(100% - 40px)',
    marginLeft: '10px',
    marginTop: '10px',
    padding: '8px',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  };

  return (
    <>

    <nav
      className="navbar"
      style={{
        backgroundColor: '#FFB400', 
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '10px 20px',
        color: '#fff'
      }}
    >
      <div style={{ fontWeight: 'bold', fontSize: '24px' }}>ü¶¥ FetchAFriend</div>

      {toastName && (
        <div style={{ position: 'absolute', left: '50%', transform: 'translateX(-50%)', color: '#70542f', fontWeight: 'bold', fontSize: '24px' }}>
          üëã Welcome, {toastName}!
        </div>
      )}

      <div className="nav-buttons" style={{ display: 'flex', gap: '10px' }}>
        <button onClick={() => setShowFavorites(!showFavorites)}>‚ù§Ô∏è Favorites ({favorites.length})</button>
        <button onClick={handleLogout}>‚èª Logout</button>
      </div>
    </nav>
      <div style={{ textAlign: 'center', marginTop: '10px' }}>
        <button
          onClick={handleFeelingLucky}
          style={{
            backgroundColor: '#FFB400',
            color: '#fff',
            padding: '10px 20px',
            border: 'none',
            borderRadius: '8px',
            fontWeight: 'bold',
            cursor: 'pointer',
            boxShadow: '0 4px 8px rgba(0,0,0,0.1)'
          }}
        >
          üé≤ Feeling Lucky?
        </button>
      </div>



      {showFavorites && (
  <div
    ref={favoritesRef}
    className="favorites-dropdown"
    style={{
      transition: 'all 0.3s ease',
      position: 'absolute',
      top: '70px',
      right: '110px',
      width: '250px',
      maxHeight: '300px',
      overflowY: 'auto',
      background: '#fff',
      border: '1px solid #ccc',
      borderRadius: '10px',
      boxShadow: '0 0 10px rgba(0,0,0,0.2)',
      zIndex: 1000,
      padding: '10px'
    }}
  >
    <h4 style={{ margin: '0 0 10px 0', textAlign: 'center' }}>‚ù§Ô∏è Favorites</h4>
    {favorites.length === 0 ? (
      <p style={{ textAlign: 'center', fontStyle: 'italic' }}>No favorites yet</p>
    ) : (
      <>
        {favorites.map(dog => (
          <div
            key={dog.id}
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '10px',
              marginBottom: '10px',
              borderBottom: '1px solid #eee',
              paddingBottom: '5px',
              position: 'relative'
            }}
          >
            <img
              src={dog.img}
              alt={dog.name}
              width="40"
              height="40"
              style={{ borderRadius: '5px', objectFit: 'cover' }}
            />
            <div style={{ flexGrow: 1 }}>
              <strong>{dog.name}</strong>
              <p style={{ fontSize: '12px', margin: 0 }}>{dog.breed}</p>
            </div>
            <button
              onClick={() => {
                const confirmRemove = window.confirm(`Are you sure you want to remove ${dog.name} from favorites?`);
                if (confirmRemove) {
                  setFavorites(favorites.filter(fav => fav.id !== dog.id));
                }
              }}
              style={{
                position: 'absolute',
                top: '0px',
                right: '0px',
                background: 'transparent',
                border: 'none',
                fontSize: '14px',
                color: '#cc0000',
                cursor: 'pointer'
              }}
            >
              ‚ùå
            </button>
          </div>
        ))}

        <button
          style={{ ...fullWidthButtonStyle, backgroundColor: '#cc0000' }}
          onClick={() => {
            const confirmClear = window.confirm('Are you sure you want to remove all favorites?');
            if (confirmClear) setFavorites([]);
          }}
        >
